import React, { Fragment, useContext, useState } from 'react';
import { Redirect } from 'react-router-dom';

import { makeStyles } from '@material-ui/core/styles';
import { TextField, Button, Icon, Typography } from '@material-ui/core';

import { AuthContext } from '../../contexts/AuthContext';

const initInfo = {
    username: '',
    email: '',
    password: '',
};

const initNotifications = {
    open: false,
    message: '',
};

const useStyles = makeStyles(theme => ({
    inputs: {
        width: '100%',
        marginTop: 20,
    },
    button: {
        width: '100%',
        marginTop: 20,
        padding: 10,
        background: 'rgb(0, 0, 0, 0.05)',
        transition: 'all 0.4s ease',
        '&:hover': {
            background: 'rgb(0, 0, 0, 0.1)',
            transform: 'scale(1.03)',
        },
    },
}));

const LoginForm = () => {

    const [info, setInfo] = useState({ ...initInfo });
    const [success, setSuccess] = useState({ ...initNotifications });
    const [error, setError] = useState({ ...initNotifications })

    const auth = useContext(AuthContext);
    const classes = useStyles();

    const handleOnSubmit = () => {
        const { email, password } = info;
        fetch('/api/users/login', {
            method: 'POST',
            body: JSON.stringify({ email, password }),
            headers: { 'Content-Type': 'application/json' },
        })
            .then(res => res.json())
            .then((result) => {
                console.log(result);
                if (result.error) {
                    return setError({ open: true, message: result.message });
                }
                auth.onLogin(result.user, result.token);
            });
    }

    return (
        <Fragment>

            <Typography variant="h4">
                Login
            </Typography>

            <TextField
                className={classes.inputs}
                variant="outlined"
                label="Username"
                value={info.username}
                onChange={(e) => setInfo({ ...info, username: e.target.value })}
            />

            <TextField
                className={classes.inputs}
                variant="outlined"
                label="Email"
                value={info.email}
                onChange={(e) => setInfo({ ...info, email: e.target.value})}
            />

            <TextField
                className={classes.inputs}
                variant="outlined"
                label="Password"
                value={info.password}
                onChange={(e) => setInfo({ ...info, password: e.target.value})}
            />

            <Button className={classes.button}>
                <Typography>
                    Submit
                </Typography>
            </Button>

        </Fragment>
    );
};

export default LoginForm;
